css:
.print-format {
    margin: 0;
    padding-top: 90mm;
    padding-left: 25mm;
    padding-right: 25mm;
}

.print-format .section {
    display: flex;
    align-items: center;
    flex-flow: column;
    position: relative;

    height: 100mm;
    padding: 5mm 0;
    /*overflow: hidden;*/
    text-align: center;
    /*border: 1px solid;*/
}
.print-format .section .content {
    flex: 2;
    display: flex;
    align-items: center;
    flex-flow: column;
}

.print-format p {
    font-size: 12pt;
}
.print-format .signature {
    border-top: 1px solid lightgray;
    padding-top: 2mm;
    padding-left: 5mm;
    padding-right: 5mm;
    font-size: 9pt;
    margin-top: 5em;
}

.print-format .doc-name {
    font-size: 9pt;
    font-weight: bold;
    margin-top: 1em;
}

.print-format .remarks {
    font-size: 8pt;
    color: grey;
}

.print-format .watermark {
    position: absolute;
    top: 50%;
    left: 50%;
    font-size: 120pt;
    transform: rotate(-20deg) translate(-50%, -50%);
    transform-origin: 0 0;
    color: rgba(0,0,0,0.3);
    z-index: 999;
}
